
$(function() {
  //$("#datepicker").datepicker();
});$(document).ready(function() {
    $('.error').hide();
    $('.submit').click(function(event) {
        $('.input').each(function (i, el) {
            var data = $(el).val();
            // console.log(i + ': ' + data);
            var len = data.length;
            if (len<1) {
                $('.error').show();
                $('html, body').animate({scrollTop:0}, 'slow');
                event.preventDefault();
                return;
            }
        });
    });
});
